}

function solution ( N, road, K )
{
  const roadsTable = {}; //ì „ì²´ ë„ë¡œ ì •ë³´

  // ë„ë¡œ ì •ë³´ ì´ˆê¸°í™” roadTable[ì‹œì‘ì ] = [ëª©ì ì§€, ê±°ë¦¬] ë°°ì—´
  for ( let i = 1; i <= N; i++ )
  {
    roadsTable[ i ] = [];
  }
  road.forEach( road =>
  {
    let [ sp, ep, dist ] = road;
    roadsTable[ sp ].push( [ ep, dist ] );
    roadsTable[ ep ].push( [ sp, dist ] );
  } );

  function djikstra ( sp )
  {
    const visited = new Array( N + 1 ).fill( false ); //ë°©ë¬¸ í™•ì¸ ë°°ì—´
    const dist = new Array( N + 1 ).fill( Infinity ); //ëª©í‘œì§€ì ê¹Œì§€ ê±°ë¦¬
    const heap = new MinHeap();

    //ì‹œì‘ì  ì‚½ì…
    heap.push( [sp, 0] );

    // ê°€ì¥ ê°€ê¹Œìš´ ëª©ì ì§€ë¶€í„° ìˆœì„œëŒ€ë¡œ ë°©ë¬¸
    while ( heap.length() > 1 )
    {
      //í™ì— ì €ì¥ëœ ëª©ì ì§€ ì¤‘ ê°€ì¥ ê°€ê¹Œìš´ ê±°ë¦¬ì˜ ëª©ì ì§€ë¥¼ êº¼ëƒ„ [ëª©ì ì§€, ê±°ë¦¬]
      const [ ep, val ] = heap.pop();
      //ì•„ì§ ë°©ë¬¸í•˜ì§€ ì•Šì€ ê³³ë§Œ ì²˜ë¦¬
      if ( !visited[ ep ] )
      {
        //ë°©ë¬¸ì²˜ë¦¬, ê±°ë¦¬ ì €ì¥
        visited[ ep ] = true;
        dist[ ep ] = val;
        //ë°©ë¬¸ ì§€ì ì„ ê±°ì³ì„œ ê°€ëŠ” ë‹¤ë¥¸ ëª©ì ì§€ êµ¬í•˜ê¸°
        const nexts = roadsTable[ ep ];
        if ( nexts )
        {
          nexts.forEach( n =>
          {
            let [ nextEp, nextVal ] = n;
            if ( !visited[ nextEp ] ) //ì•„ì§ ë°©ë¬¸í•˜ì§€ ì•Šì€ ê³³ì¼ ê²½ìš°, 'ì§€ê¸ˆê¹Œì§€ì˜ ê±°ë¦¬ + í˜„ì¬ ìœ„ì¹˜ì—ì„œì˜ ê±°ë¦¬'ë¡œ í™ì— ì‚½ì…
            {
              heap.push( [ nextEp, val + nextVal ] );
            }
          })
        }
      }
    }
    // ê±°ë¦¬ê°€ Kì´í•˜ì¸ ì§€ì ì˜ ê°œìˆ˜ ë°˜í™˜
    const result = dist.filter( d => d <= K ).length;
    return result;
  }

  const answer = djikstra( 1 );
  return answer;
}

