// ë¹›ì˜ ì´ë™ì„ ìœ„í•œ DX, DY ë³€ìˆ˜ë¥¼ ì„ ì–¸í•©ë‹ˆë‹¤.
const DX = [-1, 1, 0, 0];
const DY = [0, 0, -1, 1];

function solution(grid) {

    // DFSì— 4ë°©í–¥ì„ ê³ ë ¤í•˜ì—¬ ë¬¸ì œë¥¼ í’‰ë‹ˆë‹¤.
    // í•µì‹¬ì€ ì´ë¯¸ ë°©ë¬¸í•œ ì¹¸ì´ê³  ë°©í–¥ì´ ë™ì¼í•˜ë‹¤ë©´ í•˜ë‚˜ì˜ ì‚¬ì´í´ì´ í˜•ì„±ëœ ê²ƒìœ¼ë¡œ ìƒê°í•´ì•¼í•©ë‹ˆë‹¤.

    const answer = [];

    // visited ë³€ìˆ˜ë¥¼ ì„ ì–¸ í›„ ë°©ë¬¸ ì—¬ë¶€ë¥¼ í™•ì¸í•  3ì°¨ì› ë°°ì—´ì„ í• ë‹¹í•©ë‹ˆë‹¤. [xì¢Œí‘œ, yì¢Œí‘œ, [í•˜, ìƒ, ì¢Œ, ìš°]]
    const visited = Array.from({ length: grid.length }, () => []).map((v) => {
        for (let i = 0; i < grid[0].length; i += 1) {
            v.push(new Array(4).fill(false));
        }
        return v
    });

    for (let x = 0; x < grid.length; x += 1) {
        for (let y = 0; y < grid[0].length; y += 1) {
            for (let d = 0; d < 4; d += 1) {
                // x, y ì¢Œí‘œì— í•˜, ìƒ, ì¢Œ, ìš° ë°©í–¥ìœ¼ë¡œ ë°©ë¬¸í•œ ì ì´ ì—†ë‹¤ë©´ dfsë¥¼ ìˆ˜í–‰í•©ë‹ˆë‹¤.
                if (!visited[x][y][d]) {
                    const stack = [];
                    stack.push([x, y, d]);

                    let cnt = 0;
                    while (stack.length !== 0) {
                        const [currentX, currentY, currentD] = stack.pop();
                        if (!visited[currentX][currentY][currentD]) {
                            visited[currentX][currentY][currentD] = true;
                            cnt += 1;

                            const [nextX, nextY] = getNextXY(currentX, currentY, currentD, grid.length, grid[0].length); // ë‹¤ìŒìœ¼ë¡œ ì´ë™í•  ì¢Œí‘œë¥¼ êµ¬í•©ë‹ˆë‹¤.
                            const nextD = getNextD(grid[nextX][nextY], currentD) // x, y ì¹¸ì— ì í˜€ìˆëŠ” ë¬¸ìì—´ëŒ€ë¡œ ë°©í–¥ì„ ë‹¤ìŒ ë°©í–¥ì„ êµ¬í•©ë‹ˆë‹¤.

                            stack.push([nextX, nextY, nextD])
                        }

                    }
                    answer.push(cnt);
                }
            }
        }
    }
    return answer.sort((a, b) => a - b);
}

// ë‹¤ìŒ í–‰ì„ ì§€ë¥¼ êµ¬í•˜ëŠ” í•¨ìˆ˜
function getNextXY(x, y, d, xLength, yLength) {
    x += DX[d];
    y += DY[d];

    // xë‚˜ yì˜ ê°’ì´ ìœ íš¨í•˜ì§€ ì•Šì€ ê²½ìš° ê°’ì„ ì¬í• ë‹¹í•©ë‹ˆë‹¤.
    if (x < 0) x = xLength - 1;
    if (x >= xLength) x = 0;
    if (y < 0) y = yLength - 1;
    if (y >= yLength) y = 0;

    return [x, y];
}

// í˜„ì¬ ë°©í–¥ê³¼ ì¹¸ì— í‘œì‹œëœ ë¬¸ìë¥¼ ê¸°ì¤€ìœ¼ë¡œ ë‹¤ìŒ í–‰ì„ ì§€ì˜ ë°©í–¥ì„ êµ¬í•˜ëŠ” í•¨ìˆ˜
function getNextD(command, d) {
    if (command === 'L') {
        d = [2, 3, 1, 0][d]
    } else if (command === 'R') {
        d = [3, 2, 0, 1][d]
    }
    return d
}

