function solution(routes) {
  let cctv = 1;  // cctvì˜ ê°œìˆ˜ëŠ” ìµœì†Œ 1ê°œ
  routes.sort((a, b) => a[0] - b[0]); // ê³ ì†ë„ë¡œ ì§„ì… ì‹œì ì„ ê¸°ì¤€ìœ¼ë¡œ ì˜¤ë¦„ì°¨ìˆœ ì •ë ¬
  // [ [ -20, -15 ], [ -18, -13 ], [ -14, -5 ], [ -5, -3 ] ]
  let out = routes[0][1]; // -15
  // ë‚˜ê°„ ì‹œì (out)ì€ ì²« ì°¨ëŸ‰ì˜ ë‚˜ê°„ì‹œì ìœ¼ë¡œ ì´ˆê¸°í™”

  for(let i = 1; i < routes.length; i++) {
    // ë‚˜ê°„ ì‹œì (out)ë³´ë‹¤ í˜„ì¬ ì°¨ëŸ‰ì˜ ì§„ì…ì´ ëŠë¦¬ë‹¤ë©´ ì¹´ë©”ë¼ ì¶”ê°€ ì„¤ì¹˜
    if(out < routes[i][0]) {
      cctv++;
      out = routes[i][1]; // out ì‹œì  ì—…ë°ì´íŠ¸
    }

    // ë‚˜ê°„ ì‹œì (out)ì´ í˜„ì¬ ì°¨ëŸ‰ì˜ ì§„ì¶œì‹œì ë³´ë‹¤ í° ê²½ìš° 
    if(out > routes[i][1]) {
      out = routes[i][1]; // out ì‹œì  ì—…ë°ì´íŠ¸ 
    }
  }

  return cctv;
}
