// 1 - codeisneverodd
function solution(n, times) {
    //ìµœì†Œë¡œ ê±¸ë¦´ ìˆ˜ ìˆëŠ” ì‹œê°„ left, ìµœëŒ€ë¡œ ê±¸ë¦´ ìˆ˜ ìˆëŠ” ì‹œê°„ right
    let [left, right] = [1, Math.max(...times) * n]
    while (left <= right) {
        const mid = Math.floor((left + right) / 2)
        const sum = times.reduce((acc, time) => acc + Math.floor(mid / time), 0)
        //sumì€ mid ì‹œê°„ ë™ì•ˆ ì²˜ë¦¬ í•  ìˆ˜ ìˆëŠ” ì‚¬ëŒì˜ ìˆ˜
        if (sum < n) {
            left = mid + 1
        } else {
            right = mid - 1
        }
    }
    // left ê°€ rightë¥¼ ë„˜ì–´ê°”ë‹¤ëŠ” ê²ƒì€ leftê°€ në³´ë‹¤ í¬ê±°ë‚˜ ê°™ì•„ì ¸ì„œ nëª…ì„ ìˆ˜ìš©í•  ìˆ˜ ìµœì†Œê°’ì´ ë˜ìˆë‹¤ëŠ” ê²ƒì´ë‹¤.
    return left;
}

