//ì½”ë“œ ì°¸ê³ ìë£Œ: https://velog.io/@longroadhome/%ED%94%84%EB%A1%9C%EA%B7%B8%EB%9E%98%EB%A8%B8%EC%8A%A4-LV.4-%EB%8B%A8%EC%96%B4-%ED%8D%BC%EC%A6%90
function solution(strs, t) {
  const tLength = t.length; //ìì£¼ ì“°ëŠ” ê°’ ë¯¸ë¦¬ ê³„ì‚°
  //Infinity ë¡œ ì„ ì–¸ì„ í•´ì•¼ ì¡°í•©ì´ ë¶ˆê°€ëŠ¥í•œ ì˜ì—­ì˜ ê°’ì„ ë¬´í•œìœ¼ë¡œ ë‘ê³ , ê·¸ ì˜ì—­ì— í•˜ë‚˜ë¥¼ ë”í•´ë„ ë¶ˆê°€ëŠ¥í•˜ë‹¤ëŠ” ê²ƒì„ Infinityë¡œ í‘œí˜„í•  ìˆ˜ ìˆê²Œ ëœë‹¤.
  const minCountToIndex = new Array(tLength).fill(Infinity);
  for (let currentIndex = 0; currentIndex < tLength; currentIndex++) {
    //ë‚´ê°€ ê²€ì‚¬í•  ë¶€ë¶„ì€ tì˜ 0~currentIndex ì˜ì—­
    const currentSlice = t.slice(0, currentIndex + 1);
    for (const str of strs) {
      //í˜„ì¬ ì˜ì—­ì´ strsì— ìˆëŠ” ì¡°ê°ë“¤ ì¤‘ í•˜ë‚˜ë¡œ ëë‚œë‹¤ë©´
      if (currentSlice.endsWith(str)) {
        //frontLength ëŠ” str ì¡°ê°ì„ ì œì™¸í•œ ì• ìª½ì˜ ë‚¨ì€ ì¡°ê°ì˜ ê¸¸ì´
        const frontLength = currentIndex - str.length + 1;
        if (frontLength === 0) {
          //ì•ìª½ì— ë‚¨ì€ ê²ƒì´ ì—†ë‹¤ë©´, í˜„ì¬ ê²€ì‚¬ì¤‘ì¸ ì˜ì—­ = strsì— ìˆëŠ” ì¡°ê°
          minCountToIndex[currentIndex] = 1;
        } else {
          //ì•ìª½ì— ë‚¨ì€ ê²ƒì´ ìˆë‹¤ë©´, í˜„ì¬ ê²€ì‚¬ì¤‘ì´ ì˜ì—­ê¹Œì§€ í•„ìš”í•œ ì¡°ê° ìˆ˜ëŠ”, ì§€ê¸ˆê¹Œì§€ êµ¬í•œ ìµœì†Œ ê°’ê³¼ ì§€ê¸ˆ êµ¬í•œ ê°’ ì¤‘ ìµœì†Œê°’
          minCountToIndex[currentIndex] = Math.min(
            minCountToIndex[currentIndex],
            minCountToIndex[frontLength - 1] + 1
          );
        }
      }
    }
  }
  //ë§ˆì§€ë§‰ ì˜ì—­ì´ Infinity ì´ë©´ ë§Œë“¤ê¸° ë¶ˆê°€ëŠ¥í•œ ë‹¨ì–´, ì•„ë‹ˆë¼ë©´ ë§ˆì§€ë§‰ ì˜ì—­ì˜ ê°’ì„ ë¦¬í„´
  return minCountToIndex[tLength - 1] === Infinity
    ? -1
    : minCountToIndex[tLength - 1];
}

